package external.gw.plugin.workqueue;

/**
 * This plugin is called whenever the Batch Monitoring task discovers that all the work
for a given batch running on a workqueue has completed.
 *
 * @deprecated External entities are deprecated. Please use the new Java API instead.
 */
@Deprecated
public interface IBatchCompletedNotification extends gw.plugin.IPlugin
{

  /**
   * Called when a batch process completes...
   *
   * @param batch completed batch. Use {@link ProcessHistory#getProcessType()} to get the type of the batch process.
             Do not modify this object, it is not in a bundle so all modifications will be lost.
   * @param workItemsFailed number of failed work items generated by this batch process.
                          <code>-1</code> if there is no work queue associated with the batch process.
                          <code>0</code> if all work items succeeded.
   */
  @gw.pl.external.Invocation(type = gw.pl.external.InvocationType.METHOD, name = "completed")
  public void completed(com.guidewire.ab.external.entity.ProcessHistory batch, int workItemsFailed);

}
